namespace APP {

//${AOs::TerminalMot} ........................................................
TerminalMot TerminalMot::inst;
const char* TerminalMot::m_name = "TerminalMot";


//${AOs::TerminalMot::TerminalMot} ...........................................
TerminalMot::TerminalMot()
  : m_defined(1),
    m_motorPosition(0),
    m_knobPosition(0)
{}

//${AOs::TerminalMot::DispatchCommand} .......................................
void TerminalMot::DispatchCommand(char command) {
    // Read the incoming byte
    // Notify App if ped event selected.
    MoveEvt *pe = 0;
    KnobEvt *ke = 0;

    switch ( command ) {
        case 'f':
        case 'F':
            pe = Q_NEW(MoveEvt, FIND_LIMIT_SIG);
            pe->position = -4000;
            AO_MotionMgr->POST(pe, this);
            break;
        case 'h':
        case 'H':
            pe = Q_NEW(MoveEvt, HOME_SIG);
            AO_MotionMgr->POST(pe, this);
            break;
        case 'm':
        case 'M':
            pe = Q_NEW(MoveEvt, MOVE_SIG);
            pe->position = 1000;
            AO_Motor->POST(pe, this);
            break;
        case 'k':
        case 'K':
            RotateKnobCW();
            ke = Q_NEW(KnobEvt, KNOB_SIG);
            ke->position = m_knobPosition;
            AO_Knob->POST(ke, this);
            break;
        case 'j':
        case 'J':
            RotateKnobCCW();
            ke = Q_NEW(KnobEvt, KNOB_SIG);
            ke->position = m_knobPosition;
            AO_Knob->POST(ke, this);
            break;
        case 's':
        case 'S':
            pe = Q_NEW(MoveEvt, STOP_SIG);
            AO_Motor->POST(pe, this);
            break;
        case 'u':
        case 'U':
    #if 0
            pe = Q_NEW(MoveEvt, SHOW_STATE_SIG);
            QP::QActive::PUBLISH(pe, this);
    #endif
            consoleDisplayArgs("%s: motor = %d, knob = %d\r\n",
                m_name, m_motorPosition, m_knobPosition );

            pe = Q_NEW(MoveEvt, SYNC_SIG);
            AO_MotionMgr->POST(pe, this);
            break;
        case '+':
            m_motorPosition += 1000;
            pe = Q_NEW(MoveEvt, MOVE_SIG);
            pe->position = m_motorPosition;
            AO_Motor->POST(pe, this);
            break;
        case '-':
            m_motorPosition -= 1000;
            pe = Q_NEW(MoveEvt, MOVE_SIG);
            pe->position = m_motorPosition;
            AO_Motor->POST(pe, this);
            break;
    }
}

//${AOs::TerminalMot::RotateKnobCW} ..........................................
void TerminalMot::RotateKnobCW() {
    m_knobPosition += 1;
}

//${AOs::TerminalMot::RotateKnobCCW} .........................................
void TerminalMot::RotateKnobCCW() {
    m_knobPosition -= 1;
}

//${AOs::TerminalMot::LoadCustomEvt} .........................................
void TerminalMot::LoadCustomEvt(const CustomEvt* customEvent) {
    const PositionEvt* pe = static_cast<const PositionEvt*>(customEvent);
    int16_t position = pe->position;
    int16_t device = pe->device;
    m_motorPosition = position;

    consoleDisplayArgs("%s: received custom sig, position = %d, device = %d\r\n",
        m_name, position, device);
}

} // namespace APP
